import { NextRequest, NextResponse } from 'next/server';

const PYTHON_BACKEND_URL = 'http://localhost:5000';
const REQUEST_TIMEOUT = 10000; // 10 seconds

// Mock plot data for fallback
const mockPlots = [
  {
    plot_type: 'time_series',
    title: 'GOES XRS Time Series Data',
    image_data: '',
    description: 'Real-time X-ray flux measurements from GOES satellite showing solar flare activity over time'
  },
  {
    plot_type: 'flare_distribution',
    title: 'Solar Flare Class Distribution',
    image_data: '',
    description: 'Distribution of detected solar flares by classification (A, B, C, M, X classes)'
  },
  {
    plot_type: 'energy_analysis',
    title: 'Flare Energy Analysis',
    image_data: '',
    description: 'Energy estimation and statistical analysis of detected solar flares'
  },
  {
    plot_type: 'ml_performance',
    title: 'ML Model Performance Metrics',
    image_data: '',
    description: 'Accuracy, precision, recall and F1-score metrics for all trained ML models'
  }
];

export async function GET(request: NextRequest) {
  try {
    // Try to fetch from Python backend first
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), REQUEST_TIMEOUT);

    const response = await fetch(`${PYTHON_BACKEND_URL}/plots`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
      signal: controller.signal,
    });

    clearTimeout(timeoutId);

    if (response.ok) {
      const data = await response.json();
      return NextResponse.json(data);
    } else {
      throw new Error('Backend responded with error');
    }
  } catch (error) {
    console.warn('Python backend not available, using mock data:', error);
    
    // Return mock plot data when backend is not available
    return NextResponse.json({
      plots: mockPlots,
      status: 'mock_data',
      timestamp: new Date().toISOString(),
      message: 'Using mock plot data - Python backend not available'
    });
  }
}

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    
    // Try to forward request to Python backend
    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), REQUEST_TIMEOUT);

    const response = await fetch(`${PYTHON_BACKEND_URL}/plots`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(body),
      signal: controller.signal,
    });

    clearTimeout(timeoutId);

    if (response.ok) {
      const data = await response.json();
      return NextResponse.json(data);
    } else {
      throw new Error('Backend responded with error');
    }
  } catch (error) {
    console.warn('Python backend not available, generating mock plots:', error);
    
    // Return mock plots with simulated generation
    return NextResponse.json({
      plots: mockPlots,
      status: 'mock_generated',
      timestamp: new Date().toISOString(),
      message: 'Generated mock plots - Python backend not available'
    });
  }
}
